function callbackOne(a: number, b: number, callback: (a: number, b: number) => void) {
  callback(a, b);
}
// callbackOne이라는 함수에서 소괄호를 첫번째로 읽음
// 소괄호가 열리고 소괄호가 열리면 함수구나 인식할 것이다
// 함수가 쌓이고 있는 것이다 = 콜스택 
// 스태킹 되고 있는 것이다 = 다시부른다 = 콜백
// function callbackOne은 덧샘을 한다는 보장이 없다
// 실행 로직이 실행:호출 할때 callbackOne에서 결정한다
// 왜 콜백을 쓸까? -> 나중에 호출할때 로직을 정의하기 위해
// 데이터를 받은다음 소화시키고 뱉기 위해서
callbackOne(5, 3, (a, b) => {
  let result = a + b;         
  console.log("덧셈 결과: ", result);
});
