
function callbackFour(a: number, b: number, multiplyCallback: (result: number) => void) { // 3번
  let sum = a + b; // 4번
  multiplyCallback(sum); // 5번
}

callbackFour(5, 5, (sumResult) => { //1번 callbackFour // 2번 sumResult
  let multiplyResult = sumResult * 2; // 6번
  console.log("곱셈 결과: ", multiplyResult); //7번
});
