
function callbackFive(a: number, b: number, multiplyCallback: (result: number) => void) {
  // 콜백은 같지만 event loop에 의해서 돌아간다
  setTimeout(() => {
    let sum = a + b;
    multiplyCallback(sum);
  }, 1000);
}

callbackFive(3, 7, (sumResult) => { // 1번 // 2번
  setTimeout(() => {                // 3번
    let multiplyResult = sumResult * 3; // 4번 인자를 전달 받는다
    console.log("비동기 곱셈 결과: ", multiplyResult);
  }, 1000);
});


// 비동기는 동기로 동기는 비동기로 다 바꿔보자