// async c#에서 가져온것
// async/await를 까보면 promise가 나온다
// promise, fetch, asynce/await는 같은 맥락이다
// promise는 순서 제어를 수동으로 하겠다
// fetch는 통신용(쌀가마니 떼오기)
// 축양형 promise.all()
// 차례대로 하기 위해서 만든 것이 async/await이다
async function asyncPromiseOne(a: number, b: number): Promise<number> {
  return a + b;
}

async function main() {
  try {
    const result = await asyncPromiseOne(5, 3);
    console.log("덧셈 결과: ", result);
  } catch (error) {
    console.error("오류 발생: ", error);
  }
}

main();

