//clouser pattern
function work3(pramString:string) {
  //work2라는 함수에는 지역변수로 data가 있는데
  let data : string = "hello";
  return function(){
  // return 된 function 안에서 data의 값을 볼수 있다
    let innerData = data + pramString;
    return innerData;
  } 
}

//useState도 같은 방식으로 일어난다
let c = work3("world");
console.log(c);