
interface CallbackFourFunction {
  (a: number, b: number, multiplyCallback: (result: number) => void): void;
}

const callbackFour5: CallbackFourFunction = (a, b, multiplyCallback) => {
  let sum = a + b;
  multiplyCallback(sum);
};
callbackFour5(5, 5, (sumResult) => { //1번 callbackFour // 2번 sumResult
  let multiplyResult = sumResult * 2; // 6번
  console.log("곱셈 결과: ", multiplyResult); //7번
});
